package com.marcosapps.vendedoresml.Requestimport android.content.Contextimport android.content.SharedPreferencesimport com.marcosapps.vendedoresml.Utils.ConnectionFactoryimport com.marcosapps.vendedoresml.Utils.ConnectionReaderimport com.marcosapps.vendedoresml.Utils.MyPreferencesimport com.marcosapps.vendedoresml.Utils.UrlServidorimport org.json.JSONObjectclass BuscarInformRequest(context: Context) {    val myPreferences = MyPreferences(context)    fun executeLoginRequest(username:String): JSONObject? {        val httpsURLConnection = ConnectionFactory().createHttpsUrlConnection("GET", UrlServidor().URL_APELIDO + username)        httpsURLConnection.connect()        val statusCode = httpsURLConnection.responseCode        if (statusCode == 201            || statusCode == 200){            val apiResponse = ConnectionReader().readStringFromHttpsURLConnection(true, httpsURLConnection)            httpsURLConnection.disconnect()            val JsonResponse = JSONObject(apiResponse)            if (JsonResponse.getJSONArray("results") != null){            val id:Int = JsonResponse.getJSONObject("seller").getInt("id")            myPreferences.setChaveUser(id)            return JsonResponse            }            else {                return null            }        }        else{            //var JsonReposta = ConnectionReader().readStringFromHttpsURLConnection(false, httpsURLConnection)            return null        }    }}