package com.marcosapps.vendedoresml.Requestimport com.marcosapps.vendedoresml.Utils.ConnectionFactoryimport com.marcosapps.vendedoresml.Utils.ConnectionReaderimport com.marcosapps.vendedoresml.Utils.UrlServidorimport org.json.JSONObjectclass BuscarPersonRequest {    fun executePersonRequest(IdUser:Int?): JSONObject? {        val httpsURLConnection = ConnectionFactory().createHttpsUrlConnection("GET", UrlServidor().URL_SERVERUSER + IdUser)        httpsURLConnection.connect()        val statusCode = httpsURLConnection.responseCode        if (statusCode == 201            || statusCode == 200){            val apiResponse = ConnectionReader().readStringFromHttpsURLConnection(true, httpsURLConnection)            httpsURLConnection.disconnect()            val JsonResponse = JSONObject(apiResponse)            if (JsonResponse.has("id")){                return JsonResponse            }            else {                return null            }        }        else{            //var JsonReposta = ConnectionReader().readStringFromHttpsURLConnection(false, httpsURLConnection)            return null        }    }}