package com.marcosapps.vendedoresml.Utilsimport java.io.IOExceptionimport java.net.HttpURLConnectionimport java.net.URLimport javax.net.ssl.HttpsURLConnectionclass ConnectionFactoryHttp {    @Throws(IOException::class)    fun createHttpsUrlConnection(httpMethod: String, urlServidor: String) : HttpURLConnection {        val httpURLConnection = URL(urlServidor).openConnection() as HttpURLConnection        httpURLConnection.connectTimeout = 0        httpURLConnection.readTimeout = 0        //Setar o tipo de m√©todo recebido (GET, POST, etc)        httpURLConnection.requestMethod = httpMethod        //Setar a propriedade do Request        httpURLConnection.setRequestProperty("Content-Type", "application/json")        if (httpMethod == "POST") {            httpURLConnection.doOutput = true        }        /*if (token != null) {            //Setar o token como value da key Authorization            httpsURLConnection.setRequestProperty("Authorization", token)        }*/        return httpURLConnection    }}