package com.marcosapps.vendedoresml.Viewsimport android.support.v7.app.AppCompatActivityimport android.os.Bundleimport android.view.Viewimport android.widget.EditTextimport com.marcosapps.vendedoresml.Rimport com.marcosapps.vendedoresml.Task.BuscarInforUsuarioimport com.marcosapps.vendedoresml.Utils.ConnectionMobileimport com.marcosapps.vendedoresml.Utils.DialogCustomclass SearchPersonActivity : AppCompatActivity() {    var apelido = ""    var isPlay:Boolean = false    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        supportActionBar?.hide()    }    fun searchPerson(view: View){        val editApelido = findViewById(R.id.editApelido) as EditText        apelido = editApelido.text.toString()            if (apelido.isNotEmpty()) {                if (ConnectionMobile().isConected(this)) {                    BuscarInforUsuario(this).execute(apelido)                } else {                    DialogCustom().DialogMessage(this, getString(R.string.sem_conexao), getString(R.string.desc_conexao))                }            } else {                    DialogCustom().DialogMessage(this, getString(R.string.title_campos), getString(R.string.desc_campos))            }        }}