package com.marcosapps.vendedoresml.Viewsimport android.content.Intentimport android.support.v7.app.AppCompatActivityimport android.os.Bundleimport android.view.Viewimport android.widget.Toastimport com.marcosapps.vendedoresml.Banco.ProductsQueriesimport com.marcosapps.vendedoresml.Rimport java.util.concurrent.TimeUnitclass SplashScreenActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_splash_screen)        View.SYSTEM_UI_FLAG_FULLSCREEN;        supportActionBar?.hide();        if (ProductsQueries(this).temDadosGravados()) {            intent = Intent(this, DashboardPersonActivity::class.java)            this.finish()            startActivity(intent)        }        else{            contentOnCreate()        }    }    fun contentOnCreate() {        Thread(Runnable {            kotlin.run {                TimeUnit.SECONDS.sleep(5)                val intent = Intent(this, SearchPersonActivity::class.java)                this.finish()                startActivity(intent)            }        }).start()    }}